/*Table structure for table `jw_news` */

DROP TABLE IF EXISTS `jw_news`;

CREATE TABLE `jw_news` (
  `news_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '内部id, 自增, 主键',
