  `news_desc` varchar(255) DEFAULT NULL COMMENT '新闻简要描述, 可有可无',
  `news_author` varchar(50) DEFAULT NULL COMMENT '作者 ',
  `news_image` varchar(255) DEFAULT NULL COMMENT '图片URL',
  `news_text` text COMMENT '新闻正文',
  `thumbs` int(11) DEFAULT NULL COMMENT '点赞数, 不用爬取',
  `news_score` float DEFAULT NULL COMMENT '评分, 与热度挂钩项, 不用爬取',
  `news_post` date DEFAULT NULL COMMENT '以发布时间为准, 不用爬',
  `news_fetch` datetime DEFAULT NULL COMMENT '爬取的新闻存入数据库的时间',
  `news_hide` int(11) DEFAULT NULL COMMENT '是否被屏蔽',
