/*Table structure for table `jw_user_interest` */

DROP TABLE IF EXISTS `jw_user_interest`;

CREATE TABLE `jw_user_interest` (
  `user_id` int(10) DEFAULT NULL COMMENT '用户ID',
  `interest_code` int(2) DEFAULT NULL COMMENT '栏目代码',
  KEY `FK_INTEREST` (`interest_code`),
  KEY `FK_USER_INTEREST` (`user_id`),
  CONSTRAINT `FK_INTEREST` FOREIGN KEY (`interest_code`) REFERENCES `jw_base_interest` (`code`),
  CONSTRAINT `FK_USER_INTEREST` FOREIGN KEY (`user_id`) REFERENCES `jw_user` (`user_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `jw_user_interest` */

LOCK TABLES `jw_user_interest` WRITE;

UNLOCK TABLES;

/*Table structure for table `jw_user_log` */

DROP TABLE IF EXISTS `jw_user_log`;

CREATE TABLE `jw_user_log` (
  `user_id` int(10) DEFAULT NULL COMMENT '用户ID',
  `login_time` datetime DEFAULT NULL COMMENT '登录时间',
  `login_out` datetime DEFAULT NULL COMMENT '登出时间',
  KEY `FK_USER_LOG` (`user_id`),
  CONSTRAINT `FK_USER_LOG` FOREIGN KEY (`user_id`) REFERENCES `jw_user` (`user_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `jw_user_log` */

LOCK TABLES `jw_user_log` WRITE;

